    pressure = bmp.readPressure();

    // Next, display the temperature on the OLED
  ❼ TCA9548A(1); // Select I2C bus 1 for the OLED
    u8g2.clearBuffer(); // Clear OLED memory
    u8g2.setCursor(0, 32);
