{
    DDRD = B11111111; // Set PORTD (digital 7 through 0) to outputs
}

void loop()
{
  ❶ for (int k = 0; k < 8; k++)
    {
      ❷ PORTD = 1 << k;
         delay(100);
    }

  ❸ for (int k = 6; k > 0; —k)
    {
        PORTD = 1 << k;
        delay(100);
    }
} 
