// Project #14 - Watchdog timer

❶ #define heartBeatPin 5

❷ void heartBeat()
{
    digitalWrite(heartBeatPin, HIGH);
    delay(100);
    digitalWrite(heartBeatPin, LOW);
}

❸ void setup()
{
    pinMode(heartBeatPin, OUTPUT);
    heartBeat(); // Send heartbeat signal to watchdog timer
}

void loop()
{
    // Do things
  ❹ heartBeat(); // Send heartbeat signal to watchdog timer
} 
