// An Arduino ″Blink″ program.

int led = 13;

void setup()
{
    pinMode( led, OUTPUT );
}

void loop() 
{
  digitalWrite( led, HIGH );   // Turn on the LED
  delay( 500 );                // Wait for 1/2 second
  digitalWrite( led, LOW );    // Turn off the LED
  delay( 500 );                // Wait for a second
}
