```
void alarm(char message[])
{
  lcd.setCursor(0, 1);
  lcd.print("           ");
  delay(100);
  lcd.setCursor(0, 1);
  lcd.print(message);
  if (!mute)
  {
    tone(buzzerPin, 1000);
  }
  delay(100);
}
```

