func parseGroupNames(args []string) map[string]struct{} {
    groups := make(map[string]struct{})

    for _, arg := range args {
        grp, err := 1user.LookupGroup(arg)
        if err != nil {
            log.Println(err)
            continue
        }

        groups[2grp.Gid] = struct{}{}
    }

    return groups
}
