struct node {

    struct node *next;

    // data

};

struct node *head;

struct node *node_to_delete;

struct node *current;

struct node *previous;

previous = (struct node *)0;

current = head;

while (current != (struct node *)0) {

    if (current == node_to_delete) {

        if (previous == (struct node *)0)

            head = current->next;

        else

            previous->next = current->next;

        break;

    }

    else {

            previous = current;

            current = current->next;

    }

}
