async function

get_album_art()

{

  var data = await post("some-web-server, { artist: artist_name, album: album_name } );

  if (data.id) {

    var data = await post("some-web-server, { id: data.id });

    if (data.url)

      $(body).append('<img src="' + data.url + '"/>');

  }

}
